mapper.select:
     select * 
     from (select rownum rn, a.* 
           from (select * from unify_board order by post_date desc) a
          ) 
	 where rn between #{start} and  #{end}     
	 and board_no = 1

mapper.update:
update unify_board set 
post_title = #{post_title} , 
post_content=#{post_content}
where board_no=1 
and post_no=#{post_no}
